mapper.select:

select bom_code, product_name
from(
        select rownum as rn, bom_code, product_name
        from tbl_bom
        where rownum <= #{pageNum} * #{amount}
    )
where rn > (#{pageNum} - 1) * #{amount}
order by bom_code


select bom_code, product_name
from(
        select rownum as rn, bom_code, product_name
        from tbl_bom
        where rownum <= (#{pageNum} * #{amount}) and product_name like (#{product_name})
    )
where rn > (#{pageNum} - 1) * #{amount}
order by bom_code
